import React, {useRef, useEffect}   from "react";
import Navbar from "../components/Navbar";
import { Helmet } from "react-helmet";
import Footer from "../components/Footer";
import "../pages/styles/facilities.css";
import "../components/styles/buttons.css";
import {  TweenMax, Power3 } from 'gsap';
import Media from "react-media";






function Facilities() {

    let image = useRef(null);
    let title = useRef(null);

    useEffect(()=>{
        TweenMax.to(
            image,
            2,
            {
                opacity: 1,
                y:-30,
                ease: Power3.easeOut
            }
        )
        TweenMax.to(
            title,
            2,
            {
                opacity: 1,
                y:-30,
                ease: Power3.easeOut,
                delay: 0.2
            }
        )
    })
    //Changing backgrond header
    changeBg1 = e => {
        document.getElementById('header-fa').style.backgroundImage = `url(https://i.imgur.com/SlUpw9e.jpg)`
    };

    changeBg2 = e => {
        document.getElementById('header-fa').style.backgroundImage = `url(https://i.imgur.com/auB0347.jpg)`
    };

    changeBg3 = e => {
        document.getElementById('header-fa').style.backgroundImage = `url(https://i.imgur.com/2Qv4m9X.jpg)`
    };

    changeBg4 = e => {
        document.getElementById('header-fa').style.backgroundImage = `url(https://i.imgur.com/C5KBxhF.jpg)`
    };

    changeBg5 = e => {
        document.getElementById('header-fa').style.backgroundImage = `url(https://i.imgur.com/XxqDAl2.jpg)`
    };

    changeBg6 = e => {
        document.getElementById('header-fa').style.backgroundImage = `url(https://i.imgur.com/0WtDwfF.jpg)`
    };

    changeBg7 = e => {
        document.getElementById('header-fa').style.backgroundImage = `url(https://i.imgur.com/agkUaKG.jpg)`
    };



    return (

        <React.Fragment>
            <Navbar></Navbar>
                <div ref={selector =>{image = selector}} id='header-fa' className='header-fa'>
                    <div ref={selector =>{title = selector}} className="header-info-fa">
                    <h2 className='title-fa'>Nuestras instalaciones</h2>
                    <hr/>
                    <p ref={selector =>{title = selector}} className='paragraph-fa'>Contamos con cubículos y equipos 
                    altamente equipados. <br/>
                    Haz click en las diferentes imagenes para conocer más.</p>
                    </div>
                    <div ref={selector =>{image = selector}} className="wrapper-carrusel">
                    <div className="carrusel-img">
                        <div onClick={this.changeBg1} className="image1 img"></div>
                        <div onClick={this.changeBg2} className="image2 img"></div>
                        <div onClick={this.changeBg3} className="image3 img"></div>
                        <div onClick={this.changeBg4} className="image4 img"></div>
                        <div onClick={this.changeBg5} className="image5 img"></div>
                        <div onClick={this.changeBg6} className="image6 img"></div>
                        <div onClick={this.changeBg7} className="image7 img"></div>
                    </div>    
                    </div>
                </div>
            <Footer></Footer>
        </React.Fragment>
    ) 

}



export default Facilities;
